sys_variable_value.value:
(function execute(inputs, outputs) {
	var inputScope = inputs.flow_scope_sys_id;
    var newFlow = inputs.new_flow_sys_id;
    //var catalogItem = inputs.catalog_item;
    var connection = inputs.okta_connection;
    var activateUser = inputs.activate_user;

    //Update the catalog item 
   if(inputs.catalog_item != null){
    sn_flow.FlowTemplates.updateFlowActions(newFlow,'1', 'template_catalog_item', inputs.catalog_item);
   }
  // update folderpath blobName
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '2', 'send_activation_email', activateUser);

  if(connection){
    new global.FlowTemplatesHelper().activateConnection(connection);
  }
})(inputs, outputs);

